mapper.update:
update friends_info set friend_remark_name = #{friendRemarkName}, is_accredit_visible = #{isAccreditVisible},
accredit_startTime = #{accreditStartTime}, accredit_endTime = #{accreditEndTime}
where phone_no = #{phoneNo} and friend_phone_no = #{friendPhoneNo}

update friends_info set friend_remark_name = #{friendRemarkName}, is_authorized_visible = #{isAuthorizedVisible},
authorized_accredit_startTime = #{authorizedAccreditStartTime}, authorized_accredit_endTime = #{authorizedAccreditEndTime}
where phone_no = #{phoneNo} and friend_phone_no = #{friendPhoneNo}
