mapper.select:
select id, phone_no phoneNo, longitude, latitude, date_format(modify_time,'%Y-%m-%d %h:%i:%s') modifyTime from member_location_info_${flag} where phone_no = #{phoneNo}
and date_format(modify_time,'%Y-%m-%d %h:%i:%s') > #{modifyTime},
order by modify_time desc limit 1

select longitude, latitude from member_location_info_${flag} where phone_no = #{phoneNo}
and date_format(modify_time,'%Y-%m-%d %h:%i:%s') between #{startTime} and #{endTime}
order by id desc
